DROP TABLE IF EXISTS `sadmin_maintain`;
CREATE TABLE `sadmin_maintain` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `options_type` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '位置类型.tran:0=首页轮播,1=侧栏1,2=侧栏.code:0=Home Carousel figure,1=Left Side,2=Right Side.',
  `show_type` tinyint(4) NOT NULL DEFAULT '0' COMMENT '显示类型.tran:0=图片,2=文字,3=Markdown.0=Image,2=Textarea,3=Markdown.',
  `name` varchar(50) NOT NULL COMMENT '名称',
  `value` varchar(255) NOT NULL COMMENT '值',
  `sign` varchar(50) NOT NULL COMMENT '标识',
  `url` varchar(255) NOT NULL COMMENT 'URL',
  `info` varchar(255) NOT NULL COMMENT '备注',
  `sort` int(11) NOT NULL COMMENT '排序',
  `add_time` int(11) NOT NULL DEFAULT '0' COMMENT '添加时间',
  `edit_time` int(11) NOT NULL DEFAULT '0' COMMENT '修改时间',
  `status` tinyint(4) NOT NULL DEFAULT '1' COMMENT '状态.tran:0=禁用,1=启用.code:0=Forbbiden,1=Available.',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8 COMMENT='运营';

